    private void saveNewBurgerItemsButtonMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_saveNewBurgerItemsButtonMouseClicked
        frame = new JFrame("Exit");
        if (JOptionPane.showConfirmDialog(frame, "Are you sure you want to add the new item(s)?", "Save Confirmation", JOptionPane.YES_NO_OPTION) == JOptionPane.YES_NO_OPTION) {
        }
// <editor-fold>//saveIngredients             
        if (burgerIng1TextField.getText().isEmpty() == false) {
            FinalProject.bingredients1.add(burgerIng1TextField.getText());
            saveBIng1();
            burgerIng1TextField.setText("");
        }
        if (burgerIng2TextField.getText().isEmpty() == false) {
            FinalProject.bingredients2.add(burgerIng2TextField.getText());
            saveBIng2();
            burgerIng2TextField.setText("");
        }
        if (burgerIng3TextField.getText().isEmpty() == false) {
            FinalProject.bingredients3.add(burgerIng3TextField.getText());
            saveBIng3();
            burgerIng3TextField.setText("");
        }
        if (burgerIng4TextField.getText().isEmpty() == false) {
            FinalProject.bingredients4.add(burgerIng4TextField.getText());
            saveBIng4();
            burgerIng4TextField.setText("");
        }
        if (sideTextField.getText().isEmpty() == false) {
            FinalProject.sideOrder.add(sideTextField.getText());
            saveSide();
            sideTextField.setText("");
        }
        if (drinkTextField.getText().isEmpty() == false) {
            FinalProject.drinkOrder.add(drinkTextField.getText());
            saveDrink();
            drinkTextField.setText("");
        }
// </editor-fold>//ingredient arrays
    }//GEN-LAST:event_saveNewBurgerItemsButtonMouseClicked

