    public static void saveBIng1() {
        try {
            FileWriter fb1 = new FileWriter("savedIngredients/bingredients1.txt");
            BufferedWriter bb1 = new BufferedWriter(fb1);
            for (int i = 0; i < FinalProject.bingredients1.size(); i++) {
                bb1.write(FinalProject.bingredients1.get(i) + "\n");
            }
            bb1.close();
        } catch (IOException e) {
            System.out.println("The save was unsuccessful.");
        }
    }

